import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import { getDetail } from "../actions";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSeedling , faStar} from '@fortawesome/free-solid-svg-icons'

export default function RecipeDetail() {
    const dispatch = useDispatch();
    const { ID } = useParams();
    useEffect(() => {
        dispatch(getDetail(ID));
    }, [dispatch, ID]);

    const detailedRecipe = useSelector(state => state.detail)
    console.log(detailedRecipe)

    return (
        <div className={style.component}>
            {
                detailedRecipe.length > 0 ?
                    <div>
                        <h1>{detailedRecipe[0].name}</h1>
                        <div>
                            <img src={detailedRecipe[0].image} alt='img not found'
                                width="500px" height="400px"  />
                        </div>
                        <div>
                            <h3 >Score: {detailedRecipe[0].score} <FontAwesomeIcon icon={faStar} size="1x" /> </h3>
                            <h3 >Healthy-Food level: {detailedRecipe[0].healthScore} <FontAwesomeIcon icon={faSeedling} size="1x" /></h3>
                            <h3>Step-by-step:</h3>
                            <p  dangerouslySetInnerHTML={{ __html: detailedRecipe[0].instructions }}></p>
                            <h3>Resume:</h3><p  dangerouslySetInnerHTML={{ __html: detailedRecipe[0].summary }}></p>
                            <h3 >Diet types:</h3><ul >{detailedRecipe[0].diets.map(d => <li>{d.name}</li>)}</ul>
                        </div>
                    </div> :
                    <p>Loading...</p>
            }
        </div>
    )
};






