import React from "react";
import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { getRecipeType, postRecipe } from "../actions";
import { useDispatch } from "react-redux";
import styles from "../components/Styles/recipeCreator.module.css";
import pic1 from "../components/pics/K1.jpeg";

export default function RecipeCreator() {
  const dispatch = useDispatch();
  //const history = useHistory();
  // const diets = useSelector((state) => state.diets);
  const [err, setErr] = useState({});

  function validate(input) {
    let err = {};
    if (!input.name) {
      err.name = "Name must be filled";
    }
    if (!input.resume) {
      err.resume = "Resume must be filled";
    }
    return err;
  }

  const [input, setInput] = useState({
    name: "",
    resume: "",
    score: "",
    healthylevel: "",
    stepbystep: "",
    image: "",
    diets: [],
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    setErr(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleCheck(e) {
    if (e.target.checked) {
      setInput({
        ...input,
        diets: [...input.diets, e.target.value],
        //diets: e.target.value,
      });
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postRecipe(input));
    alert("Recipe successfully created");
    setInput({
      name: "",
      resume: "",
      score: "",
      healthylevel: "",
      stepbystep: "",
      image: "",
      diets: [],
    });
    //history.push("/home");
  }

  useEffect(() => {
    dispatch(getRecipeType());
  }, [dispatch]);

  return (
    <div>
      <img className={styles.img1} src={pic1} alt="1"></img>
      <NavLink to="/home">
        <button className={styles.botn}>Volver</button>
      </NavLink>
      <h1 className={styles.titulo}>Crea tu Receta</h1>
      <form className={styles.formframe} onSubmit={(e) => handleSubmit(e)}>
        <div className={styles.n}>
          <label>Name</label>
          <input
            className={styles.i1}
            type="text"
            value={input.name}
            name="name"
            onChange={(e) => handleChange(e)}
          />
          {err.name && <p className={styles.err}>{err.name}</p>}
        </div>
        <div className={styles.r}>
          <label>Resume</label>
          <input
            className={styles.i2}
            type="text"
            value={input.resume}
            name="resume"
            onChange={(e) => handleChange(e)}
          />
          {err.resume && <p className={styles.err2}>{err.resume}</p>}
        </div>
        <div className={styles.s}>
          <label>Score</label>
          <input
            className={styles.i3}
            type="number"
            value={input.score}
            name="score"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className={styles.HS}>
          <label>Healthy Level</label>
          <input
            className={styles.i4}
            type="number"
            value={input.healthylevel}
            name="healthylevel"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className={styles.SbS}>
          <label>Step by step</label>
          <input
            className={styles.i5}
            type="text"
            value={input.stepbystep}
            name="stepbystep"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className={styles.i}>
          <label>Image</label>
          <input
            className={styles.i6}
            type="text"
            value={input.image}
            name="image"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div>
          {/* <label className={styles.dtframe}>Diet type</label> */}
          <label className={styles.dtframe2}>
            <input
