import React from "react";
import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { getRecipeType, postRecipe } from "../actions";
import { useDispatch } from "react-redux";
import Style from '../components/styles/RecipeCreator.module.css';


export default function RecipeCreator() {
  const dispatch = useDispatch();
  //const history = useHistory();
  // const diets = useSelector((state) => state.diets);
  const [err, setErr] = useState({});

  function validate(input) {
    let err = {};
    if (!input.name) {
      err.name = "Name must be filled";
    }
    if (!input.resume) {
      err.resume = "Resume must be filled";
    }
    return err;
  }

  const [input, setInput] = useState({
    name: '',
    resume: '',
    score: '',
    healthylevel: '',
    stepbystep: '',
    image: '',
    diets: []
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    setErr(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleCheck(e) {
    if (e.target.checked) {
      setInput({
        ...input,
        diets: [...input.diets, e.target.value],
        
      });
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postRecipe(input));
    alert("Recipe successfully created");
    setInput({
        name: '',
        resume: '',
        score: '',
        healthylevel: '',
        stepbystep: '',
        image: '',
        diets: []
    });
    //history.push("/home");
  }

  useEffect(() => {
    dispatch(getRecipeType());
  }, [dispatch]);

  return (
    <div className={Style.container} >
    <div className={Style.bkg}/>
    <div className={Style.bkgcolor}>
    <div className ={Style.form}>
    
      <NavLink to="/home"  >
        <button  className={Style.createButton}>Volver</button>
      </NavLink>
      <h1  >Crea tu Receta</h1>
      <form   onSubmit={(e) => handleSubmit(e)}>
        <div className={Style.n} >
          <label>Name</label>
          <input className={Style.i1}
           
            type="text"
            value={input.name}
            name="name"
            onChange={(e) => handleChange(e)}
          />
          {err.name && <p> </p>}
        </div>
        <div className={Style.r} >
          <label>Resume</label>
          <input  className={Style.i2}
           
            type="text"
            value={input.resume}
            name="resume"
            onChange={(e) => handleChange(e)}
          />
          {err.resume && <p ></p>}
        </div>
        <div className={Style.s} >
          <label>Score</label>
          <input   className={Style.i3}
       
            type="number"
            value={input.score}
            name="score"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div  className={Style.HS}>
          <label>Healthy Level</label>
          <input className={Style.i4}
            
            type="number"
            value={input.healthylevel}
            name="healthylevel"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className={Style.SbS} >
          <label>Step by step</label>
          <input  className={Style.i5}
           
            type="text"
            value={input.stepbystep}
            name="stepbystep"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className={Style.i} >
          <label>Image</label>
          <input  className={Style.i6}
            
            type="text"
            value={input.image}
            name="image"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div>
          
          {/* <label className={styles.dtframe}>Diet type</label> */}
