import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import { getDetail } from "../actions";

export default function Detail() {
    const dispatch = useDispatch();
    const { ID } = useParams();
    useEffect(() => {
        dispatch(getDetail(ID));
    }, [dispatch, ID]);

    const Recipe = useSelector(state => state.detail)
   

    return (
        <div>
            {
                Recipe.length > 0 ?
                    <div>
                        <h1>{Recipe[0].name}</h1>
                     
                            <img src={Recipe[0].image} alt='img not found'
                                width="500px" height="400px"  />
                     
                            <h5 >Healthy level: {Recipe[0].healthylevel}</h5>
                           <h5 >Score: {Recipe[0].score}</h5>
                            {/* <h3 >Score: {detailedRecipe[0].score} <FontAwesomeIcon icon={faStar} size="1x" /> </h3>
                            <h3 >Healthy-Food level: {detailedRecipe[0].healthScore} <FontAwesomeIcon icon={faSeedling} size="1x" /></h3> */}
                            <h3>Step-by-step:</h3>
                            <h5 >Healthy level: {Recipe[0].healthylevel}</h5>
                            <p ><h5>Resume: {Recipe[0].resume.replace(/<[^>]*>?/g, '')}</h5></p>
                            {/* <h3>Resume:</h3><p  dangerouslySetInnerHTML={{ __html: detailedRecipe[0].summary }}></p> */}
                            <h3 >Diet types:</h3><ul >{Recipe[0].diets.map(d => <li>{d.name}</li>)}</ul>
                        
                    </div> :
                    <p>Loading...</p>
            }
        </div>
    )
};






