  import React from "react";
  import { NavLink } from 'react-router-dom';
  import { useDispatch, useSelector } from "react-redux";
  import { getDetail } from '../actions/index'
  


  export default function Detail(props) {
    const dispatch = useDispatch();
    const recipe = useSelector(state => state.detail); 

    React.useEffect(()=>{ dispatch(getDetail(props.match.params.id)) }, [dispatch, props.match.params.id]);
        
      
    return (
        <div>
            <NavLink to="/home">Get Back</NavLink>
            {   recipe.length >= 1 
                ?
                <div>
                    
                    <h2>{recipe[0].name}.</h2>
                    <hr/>
                    <h4 > Score: {recipe[0].puntuation} points. </h4> <br/>
                    <h4 > Healthiness: {recipe[0].healthyLevel} points. </h4>
                    <hr/>
                    {
                        <div>
                            <label>Diet-types:</label>
                            {
                                recipe[0].diets && recipe[0].diets.length 
                                ? 
                                recipe[0].diets.map(diet => ` ${diet}. `) 
                                : 
                                recipe[0].diets 
                                ? 
                                " No diet-type specified for this recipe, sorry..." 
                                :
                                recipe[0].dietTypes && recipe[0].dietTypes.length 
                                ? 
                                recipe[0].dietTypes.map(diet => ` ${diet.name}. `) 
                                :
                                " No diet-type specified for this recipe, sorry..."
                            }
                        </div>
                    }
                    <hr/>
                    <p > Summary: {recipe[0].resumePlate.replace(/<[^>]*>?/g, '')}. </p>
                    <img  src={recipe[0].image} alt="" width="200px" height="200px" />
                    <hr/>
                    <p > Step by step: {recipe[0].stepByStep ? recipe[0].stepByStep : " No step-by-step specified for this recipe, sorry..."}. </p> 
                </div>
                :
                <div>
                 
                <NavLink to='/home'> <button >Back to home</button> </NavLink>
                </div>
            }
        </div>
    );
};

   
