import React from 'react';
import { NavLink, useNavigate} from 'react-router-dom';
import { getRecipeType, postRecipe  } from '../../actions/index';
import { useDispatch, useSelector } from 'react-redux';
import { GoCheck } from "react-icons/go";


const RecipeCreator =()=> {

    const dispatch = useDispatch();
    const dietTypes = useSelector(state => state.dietTypes);
    const history =useNavigate();

    React.useEffect(()=>{ dispatch(getRecipeType()) }, [dispatch]);

    const [errors, setErrors] = React.useState({});
    const [habilitado, setHabilitado] = React.useState(false);

    const inputValidate = input => {
        const errors = {};
    
        if (!input.name) {
            errors.name = "Recipe's name is required!";
            setHabilitado(false)
        }
        if (!input.resume) {
            errors.resume = "Recipe's summary is required!";
            setHabilitado(false)
        }
        else setHabilitado(true);
    
        return errors;
    };

    const [input, setInput] = React.useState({
        name: "", 
        resumePlate: "", 
        puntuation: 0, 
        healthyLevel: 0, 
        stepByStep: "", 
        diets: [],
        image: ""
    });

    const typing = evt => {
        evt.preventDefault();
        setInput({
            ...input,
            [ evt.target.name ] : evt.target.value
        });
        setErrors(inputValidate({
            ...input,
            [ evt.target.name ] : evt.target.value
        }))
    };

    const submiting = evt => {
        evt.preventDefault();
        dispatch(postRecipe(input));
        alert("Recipe created succesfully!");
        setInput({
            name: "", 
            resume: "", 
            score: 0, 
            healthyLevel: 0, 
            stepByStep: "", 
            image: "",
            diets: [],
        });
        history.push("/home");
    };

    const selection = evt => {
        setInput({
            ...input,
            diets: [...input.diets, evt.target.value]
        });
    };

    return (
        <div>
            <NavLink to="/home" >Get back</NavLink>

            <form>
                <div>
                    <input 
                    placeholder="Name of your recipe...*" 
                    value={input.name} 
                    name="name"
                    onChange={evt =>{ typing(evt) }}
                    autoComplete="off" 
                     />
                    { errors.name && ( <div >{errors.name}</div> ) }
                </div>
                <div>
                    <textarea 
                    placeholder="Summary of your recipe...*" 
                    value={input.resumePlate} 
                    name="resumePlate"
                    onChange={evt =>{ typing(evt) }}
                     />
                     { errors.resumePlate && ( <div >{errors.resumePlate}</div> ) }
                </div>
                <div>
                    <label>Score 0/100: </label>
                    <input 
                    type="range" 
                    min="1" max="100" 
                    value={input.puntuation} 
                    name="puntuation" 
                    onChange={evt =>{ typing(evt) }}
                    />
                    <span>{input.puntuation ? input.puntuation : 0} points</span>
                </div>
                <div>
                    <label>Healthiness 0/100: </label>
                    <input 
                    type="range" 
                    min="1" max="100" 
                    value={input.healthyLevel} 
                    name="healthyLevel" 
                    onChange={evt =>{ typing(evt) }}
                    />
                     <span>{input.healthyLevel ? input.healthyLevel : 0} points</span>
                </div>
                <div>
                    <textarea 
                    placeholder="Step by step of your recipe..." 
                    value={input.stepByStep} 
                    name="stepByStep"
                    onChange={evt =>{ typing(evt) }}
                    />
                </div>
                <div>
                    <input 
                    placeholder="Image URL of your recipe..." 
                    type="text" 
                    value={input.image} 
                    name="image"
                    onChange={evt =>{ typing(evt) }}
                    autocomplete="off" 
                   />
                </div>
                <div>
                    <label>Diet-Types: </label>
                    <select name="dietType" onChange={evt =>{ selection(evt) }} >
                        { dietTypes && dietTypes.map(diet => (
                            <option value = { diet.name }>{ diet.name }</option> ))
                        }
                    </select>
                </div> 
                <ul ><li>{input.diets && input.diets.map(diet => `// ${diet} //`)}</li></ul>
                <div>
                    {<button 
                    type="submit" 
                    disabled={!habilitado} 
                    onClick={evt => submiting(evt)} 
                    
                    >CREATE <GoCheck/></button>}
                </div>
            </form>
        </div>
    );
};

export default RecipeCreator;









