//             <h3>{name}</h3>
//             <h5> { diets }</h5>
//             <img  src={image} alt='img not found' width='200px' height='250px'/>
   
//         </div>
//     );
// }

export default function Card({ name, img, diet, id }) {
    return (
        <div>
            <div>
                <div>
                    <h2 >{name}</h2>
                    <ul>{diet.map(d => <li key={d.name}>{d.name}</li>)}</ul>
                    <div >
                        <Link to={'/recipes/' + id}>
                        See recipe details</Link>
                    </div>
                </div>
                <img src={img} alt="not found" width="300px" height="300px"/>
            </div>
            <div></div>
        </div>
    )
}
