const initialState = {
    recipes: [],
    //  allRecipes: [],
    //  diets: [],
    //  detail: []
}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'GET_RECIPES':
            return {
                ...state,
                recipes: action.payload,
                
            }
        default:
            return { ...state};
        
    }
}

export default rootReducer;
